mapper.update:
UPDATE style_2 SET
rcount=rcount+1
WHERE style_id=#{sid}

UPDATE style_reply_2 SET
group_tab=group_tab+1
WHERE sreply_id=#{sid}

mapper.select:
SELECT group_id, group_step, group_tab
FROM style_reply_2
WHERE sreply_id=#{sreply_id}

mapper.update:
UPDATE style_reply_2 SET
group_step=group_step+1
WHERE group_id=#{group_id}

UPDATE style_reply_2 SET
depth=depth+1
WHERE sreply_id=#{sreply_id}
